// 로그인이 되었는지 안되었는지 확인
// 로그인이 되어있다면 > main으로 넘어가면 되겠고
// 안되어 있다면 > 로그인 페이지로 넘어가면 됩니다


<template>
<!-- src="https://cdn.vuetifyjs.com/images/backgrounds/vbanner.jpg" -->
<!-- <v-card
    class="d-flex align-center justify-center mb-6 bg-surface-variant"
    height="100"
  >
    <v-sheet
    v-for="n in 3"
    :key="n"
    class="ma-2 pa-2"
    >
    align-start
    </v-sheet>
</v-card> -->

<v-parallax
    align="center"
    :height="windowHeight"
    :src="require('@/assets/wallpaper.jpg')"
  >
    <div class="d-flex flex-column fill-height justify-center align-center text-white">
        <v-card class="elevation-12 transparent-body" width="50%">
            <!-- title -->


            <!-- body -->
            <v-card-text >
                <v-form>
                    <v-text-field
                        v-model="username"
                        class = "text-white"
                        name="username"
                        label="Email/ID"
                        type="text"
                    ></v-text-field>
                    <v-text-field
                        v-model="password"
                        id="password"
                        class = "text-white"
                        name="password"
                        label="Password"
                        type="password"
                        color = "white"

                    ></v-text-field>
                </v-form>
            </v-card-text>
            <!-- buttons -->
            <v-row>
                <v-spacer></v-spacer>
                <v-btn color="white" @click="Login">Sign In</v-btn>
                <a id = "register" class="ma-4" @click="Register" > or Register </a>

            </v-row>

            <v-card-actions>
                <v-spacer></v-spacer>
                <v-btn color="white" @click="Login">Sign In</v-btn>
                <a id = "register" class="ma-4" @click="Register" > or Register </a>

            </v-card-actions>
        </v-card>
    </div>
</v-parallax>


</template>

<script>
import axios from "axios";
let url = "http://127.0.0.1:8000/api/login";

export default {
    data : () => {
        return {
            username: "",
            password : "",
            windowHeight : window.innerHeight
                 
        };
    },


   methods : {
       Login :function() {
            axios({
                method: "POST",
                url: url, // localhost:8000/user/1 로 delete method
                data: this.data,
            })
                .then((response) => {
                    console.log(response.data.user);
                    localStorage.setItem('access_token', response.data.access_token);
                    localStorage.setItem('refresh_token', response.data.refresh_token);
                    localStorage.setItem('user', response.data.user.id);
                    // js redirect
                    // window.location.href = 'http://localhost:8080/';
                    
                    // vue
                    //this.$router.push({ name : 'home'})
                    this.$router.go(this.$router.currentRoute);
                })
                .catch((error) => {
                    console.log("Failed to get userList", error.response);
                });
        },
    Register :function() {
        this.$router.push({ name : 'register'})
        },
   },

  mounted(){
    window.onresize = () => {
      this.windowHeight = window.innerHeight
    }
  },





};
</script>

<style>
#register {
    margin-left : auto ;
    color : white;
}

.transparent-body {
    background: rgba(0, 0, 0, 0.2);
    backdrop-filter: blur(10px);
    /* color : rgb(255,255,255); 기본 배경 및 텍스트 컬러 */
    
    /* color : rgb(0,0,0) */

}
</style>
