    <div class="d-flex flex-column fill-height justify-center align-center text-white">
        <v-card class="elevation-12" width="50%">
            <!-- title -->
            <v-toolbar dark color="primary">
                <v-toolbar-title>
                    Login form
                </v-toolbar-title>
                <a id = "register" class="ma-4" @click="Register" > or Register </a>
            </v-toolbar>

            <!-- body -->
            <v-card-text class ="transparent-body">
                <v-form>
                    <v-text-field
                        v-model="username"
                        name="username"
                        label="Email/ID"
                        type="text"
                    ></v-text-field>
                    <v-text-field
                        v-model="password"
                        id="password"
                        name="password"
                        label="Password"
                        type="password"
                    ></v-text-field>
                </v-form>
            </v-card-text>
            <!-- buttons -->
            <v-card-actions>
                <v-spacer></v-spacer>
                <v-btn color="primary" @click="Login">Login</v-btn>
            </v-card-actions>
        </v-card>
    </div>
</v-parallax>


</template>

<script>
import axios from "axios";
let url = "http://127.0.0.1:8000/api/login";

export default {
    data : () => {
        return {
            username: "",
            password : "",
            windowHeight : window.innerHeight
                 
        };
    },


   methods : {
       Login :function() {
            axios({
                method: "POST",
                url: url, // localhost:8000/user/1 로 delete method
                data: this.data,
            })
                .then((response) => {
                    console.log(response.data.user);
                    localStorage.setItem('access_token', response.data.access_token);
                    localStorage.setItem('refresh_token', response.data.refresh_token);
                    localStorage.setItem('user', response.data.user.id);
                    // js redirect
                    // window.location.href = 'http://localhost:8080/';
                    
                    // vue
                    //this.$router.push({ name : 'home'})
                    this.$router.go(this.$router.currentRoute);
                })
                .catch((error) => {
                    console.log("Failed to get userList", error.response);
                });
        },
    Register :function() {
        this.$router.push({ name : 'register'})
        },
   },

  mounted(){
    window.onresize = () => {
      this.windowHeight = window.innerHeight
    }
  },





};
</script>

