</v-parallax> -->

<v-parallax dark src="https://townshipcanada.com/static/pattern.svg">
    <v-layout white--text align-center column justify-center style="margin-top: 8.5em;">
    <h3 class=" display-3 ma-2 text-xs-center">Vuetify.js</h3>
    <h4 class="subheading">Build your application today!</h4>
    <v-flex row wrap text-xs-center>
        <v-btn dark large color="blue lighten-2" class="mt-4 elevation-0">login</v-btn>
        <v-btn dark large color="blue lighten-2" class="mt-4 elevation-0">signup</v-btn>
    </v-flex>
    </v-layout>
</v-parallax>

</template>

<script>
import axios from "axios";
let url = "http://127.0.0.1:8000/api/login";

export default {
    data: () => {
        return {
            data: {
                username: "",
                password : ""
            },        
        };
    },

   methods : {
       Login :function() {
            axios({
                method: "POST",
                url: url, // localhost:8000/user/1 로 delete method
                data: this.data,
            })
                .then((response) => {
                    console.log(response.data.user);
                    localStorage.setItem('access_token', response.data.access_token);
                    localStorage.setItem('refresh_token', response.data.refresh_token);
                    localStorage.setItem('user', response.data.user.id);
                    // js redirect
                    // window.location.href = 'http://localhost:8080/';
                    
                    // vue
                    //this.$router.push({ name : 'home'})
                    this.$router.go(this.$router.currentRoute);
                })
                .catch((error) => {
                    console.log("Failed to get userList", error.response);
                });
        },
    Register :function() {
        this.$router.push({ name : 'register'})

            },

   }
};
</script>

<style>
#register {
    margin-left : auto ;
    color : white;
}


</style>
